import { mapState, mapActions } from 'pinia';
import userStore from '@/store/user';
export default {
  name: 'SignOut',
  computed: { ...mapState(userStore, ['user'])
  },
  methods: { ...mapActions(userStore, ['signOut']),

    handleSignOut() {
      this.signOut();
      this.$router.push({
        path: '/logout-ok'
      });
    }

  }
};
